import React, { useState, useEffect } from "react";

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");

  // Fetch todos
  useEffect(() => {
    fetch("http://localhost:5000/todos")
      .then(res => res.json())
      .then(data => setTodos(data));
  }, []);

  // Add todo
  const addTodo = () => {
    if (!text) return;
    fetch("http://localhost:5000/todos", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text })
    })
      .then(res => res.json())
      .then(newTodo => setTodos([...todos, newTodo]));
    setText("");
  };

  // Toggle complete
  const toggleComplete = (id, completed) => {
    fetch(`http://localhost:5000/todos/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ completed })
    })
      .then(res => res.json())
      .then(updatedTodo => {
        setTodos(todos.map(t => (t._id === id ? updatedTodo : t)));
      });
  };

  // Delete todo
  const deleteTodo = (id) => {
    fetch(`http://localhost:5000/todos/${id}`, { method: "DELETE" })
      .then(() => setTodos(todos.filter(t => t._id !== id)));
  };

  return (
    <div style={{ maxWidth: "500px", margin: "40px auto", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center", color: "#333" }}>My Todos</h1>

      <div style={{ display: "flex", marginBottom: "20px" }}>
        <input
          value={text}
          onChange={e => setText(e.target.value)}
          placeholder="Enter a new todo"
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "5px 0 0 5px",
            border: "1px solid #ccc",
            fontSize: "16px"
          }}
        />
        <button
          onClick={addTodo}
          style={{
            padding: "10px 20px",
            border: "none",
            backgroundColor: "#4CAF50",
            color: "white",
            fontWeight: "bold",
            borderRadius: "0 5px 5px 0",
            cursor: "pointer",
            transition: "background 0.3s"
          }}
          onMouseOver={e => e.target.style.backgroundColor = "#45a049"}
          onMouseOut={e => e.target.style.backgroundColor = "#4CAF50"}
        >
          Add
        </button>
      </div>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {todos.map(todo => (
          <li
            key={todo._id}
            style={{
              backgroundColor: todo.completed ? "#e0ffe0" : "#ffe0e0",
              padding: "10px",
              marginBottom: "10px",
              borderRadius: "5px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              boxShadow: "0 2px 5px rgba(0,0,0,0.1)"
            }}
          >
            <span style={{ fontSize: "16px", fontWeight: "500" }}>
              {todo.text}
            </span>
            <div>
              <button
                onClick={() => toggleComplete(todo._id, !todo.completed)}
                style={{
                  padding: "5px 10px",
                  marginRight: "5px",
                  backgroundColor: todo.completed ? "#FFA500" : "#4CAF50",
                  color: "white",
                  border: "none",
                  borderRadius: "3px",
                  cursor: "pointer"
                }}
              >
                {todo.completed ? "Undo" : "Done"}
              </button>
              <button
                onClick={() => deleteTodo(todo._id)}
                style={{
                  padding: "5px 10px",
                  backgroundColor: "#f44336",
                  color: "white",
                  border: "none",
                  borderRadius: "3px",
                  cursor: "pointer"
                }}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoApp;
